import { useState } from 'react'

import Head from "./components/Head"
import Navedit from './components/Navedit'
import Task from "./components/Task"
import Addtask from "./components/Addtask"

const App=()=>{
  const[add,addTask]=useState(false)
  const[edit,editTask]=useState([])
  const[task,setTask]=useState([
    {
      id:1,
      text:'Doctors Appointment',
      day:'2023-02-05',
      time:'10:00',
      reminder:true,
    },
    {
      id:2,
      text:'Meeting at School',
      day:'2023-02-05',
      time:'10:00',
      reminder:true,
    },
    {
      id:3,
      text:'Food Shopping',
      day:'2023-02-05',
      time:'10:00',
      reminder:false,
    },
  ])
  function deletetask(id){
    setTask(task.filter((task)=>task.id!==id))
  }
  function toggle(id){
    setTask(task.map((task)=>task.id===id?{
      ...task,reminder:!task.reminder}:task
    ))
  }
  function show(b){
    addTask(!b)
  }
  function edited(id){
  const  toedit=task.filter((task)=>task.id===id)
  editTask(toedit)  
}
  function addtask(ntask) {
    const id=Math.floor(Math.random()*10000)+1
    const newTask={id,...ntask}
    setTask([...task,newTask])

  }

  function edits(ntask){
    setTask([...task,ntask])
    show()

  }
  function edittask(id){
    edited(id)
    deletetask(id)
  }
  console.log(task)
  return(
    <div className="container">
      <Head title="Task-Tracker" show={show} add={add} />
      {add?<Addtask addTask={addtask}/>:''}
      {edit?<Navedit edited={edit} edits={edits}/>:''}
      {task.length>0?(<Task task={task} editTask={edittask} deletetask={deletetask}
      toggle={toggle}
      />)
      :(<p>NO TASKS</p>)}
    </div>
  )
}

export default App;
