import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [updatedTask, setUpdatedTask] = useState('');
  const [taskIdToUpdate, setTaskIdToUpdate] = useState('');

  // Function to fetch tasks from the server
  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/tasks');
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  // Function to create a new task
  const createTask = async () => {
    try {
      await axios.post('http://localhost:5000/tasks', { text: newTask });
      setNewTask('');
      fetchTasks();
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  // Function to update a task by ID
  const updateTask = async () => {
    try {
      await axios.put(`http://localhost:5000/tasks/${taskIdToUpdate}`, { text: updatedTask });
      setUpdatedTask('');
      setTaskIdToUpdate('');
      fetchTasks();
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  // Function to delete a task by ID
  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${id}`);
      fetchTasks();
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div>
      <h1>Task Tracker</h1>

      {/* Create a new task */}
      <div>
        <input
          type="text"
          placeholder="New Task"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button onClick={createTask}>Add Task</button>
      </div>

      {/* Update a task */}
      <div>
        <input
          type="text"
          placeholder="Task Text to Update"
          value={updatedTask}
          onChange={(e) => setUpdatedTask(e.target.value)}
        />
        <input
          type="text"
          placeholder="Task ID to Update"
          value={taskIdToUpdate}
          onChange={(e) => setTaskIdToUpdate(e.target.value)}
        />
        <button onClick={updateTask}>Update Task</button>
      </div>

      {/* List of tasks */}
      <ul>
        {tasks.map((task) => (
          <li key={task._id}>
            {task.text}{' '}
            <button onClick={() => deleteTask(task._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
