import React from 'react'
import { useState } from 'react'
import Addtask from "./Addtask"


const Edit = ({edited}) => {
  const[text,setText]=useState('')
  const[day,setDay]=useState('')
  const[time,setTime]=useState('')
  const[reminder,setReminder]=useState(false)

  function submit(e){
    e.preventDefault()
    if(!text){
    alert('Please Enter the Title')
  }
  else{
  addTask({text,day,time,reminder})
  }
  setText('')
  setDay('')
  setTime('')
  setReminder(false)
  }
  return (
    <div>
        {
      edited.map((edit)=>(
       <form className='add-form' onSubmit={submit}>
      <div className="form-control">
        {edit.text}
       { edit.text.length?(<input type="text" name="" className="form-control input" 
        value={edit.text}/>):(<input type="text" name="" className="form-control input" 
        value={text} onChange={(e)=>setText(e.target.value)
        } placeholder="Title"/>)}
      </div>
      <div className="form-control">
      { edit.day?(<input type="text" name="" className="form-control input" 
        value={edit.day}/>):
        (<input type="date" name="" id="" className="form-control input" 
        value={day} onChange={(e)=>setDay(e.target.value)}/>)
        }
      </div>
      <div className="form-control">
      { edit.time?(<input type="time" name="" className="form-control input" 
        value={edit.time}/>):(<input type="time" name="" id="" className="form-control input" 
        value={time} onChange={(e)=>setTime(e.target.value)}/>)
        } 
      </div>
      <div className="form-control-check">
        <label htmlFor="checkbox" className='form-control-check label'>Add Reminder</label>
        { edit.reminder?-(<input type="checkbox" name="" className="form-control input" 
        checked={edit.reminder}/>):
        (<input type="checkbox" name="" id="checkbox" checked={reminder} className="form-control-check input" 
        onChange={(e)=>setReminder(e.currentTarget.checked)}/>)
        } 
      </div>

      <button type="submit" className="btn btn-block">Add Task</button>
    </form> 
    ))
  }

    </div>
  )
}

export default Edit
