    <div className={`task ${task.reminder?'reminder':''}`} onDoubleClick={()=>toggle(task.id)}>
      <h3>{task.text}<Button color={'blue'} text={Edit} onclick={onclick}/><FaTimes style={{color:'red',cursor:'pointer'}}
      onClick={()=>deletetask(task.id)}
      /></h3>  
      <p>{task.day} at {task.time}</p>
    </div>
  )
}
export default Tasks
