import { useState } from 'react'

import Head from "./components/Head"

import Task from "./components/Task"
import Addtask from "./components/Addtask"

const App=()=>{
  const[atask,addtask]=useState(false)
  const[task,setTask]=useState([
    {
      id:1,
      text:'Doctors Appointment',
      day:'feb 5th 2023',
      time:'10:00 pm',
      reminder:true,
    },
    {
      id:2,
      text:'Meeting at School',
      day:'feb 5th 2023',
      time:'10:00 pm',
      reminder:true,
    },
    {
      id:3,
      text:'Food Shopping',
      day:'feb 5th 2023',
      time:'10:00 pm',
      reminder:false,
    },
  ])
  function deletetask(id){
    setTask(task.filter((task)=>task.id!==id))
  }
  function toggle(id){
    setTask(task.map((task)=>task.id===id?{
      ...task,reminder:!task.reminder}:task
    ))
  }
  function show(b){
    addtask(!b)
  }
  function addTask(ntask) {
    const id=Math.floor(Math.random()*10000)+1
    const newTask={id,...ntask}
    setTask([...task,newTask])
  }

  return(
    <div className="container">
      <Head title="Task-Tracker" show={show} atask={atask} />
      {atask?<Addtask addTask={addTask}/>:''}
      {task.length>0?(<Task task={task} deletetask={deletetask}
      toggle={toggle}
      />)
      :(<p>NO TASKS</p>)}
    </div>
  )
}

export default App;
