import React from 'react'
import { useState } from 'react'


const Edit = ({edited}) => {
  const[text,setText]=useState('')
  const[day,setDay]=useState('')
  const[time,setTime]=useState('')
  const[reminder,setReminder]=useState(false)

    function submit(e){
    e.preventDefault()
    if(!text){
    alert('Please Enter the Title')
  }
  else{
  edited({text,day,time,reminder})
  }
  setText('')
  setDay('')
  setTime('')
  setReminder(false)
  }
  return (
    <div>
        {
      edited.map((edit)=>(
       <form className='add-form' onSubmit={submit}>
      <div className="form-control">
    <input type="text" name="" className="form-control input" 
        value={edit.text} onChange={(e)=>setText(e.target.value)}/>
      </div>
      <div className="form-control">
    <input type="text" name="" className="form-control input" 
        value={edit.day}/><input type="date" name="" id="" className="form-control input" 
        value={day} onChange={(e)=>setDay(e.target.value)}/>
        
      </div>
      <div className="form-control">
      <input type="time" name="" className="form-control input" 
        value={edit.time} onChange={(e)=>setTime(e.target.value)}/>
      </div>
      <div className="form-control-check">
        <label htmlFor="checkbox" className='form-control-check label'>Add Reminder</label>
         <input type="checkbox" name="" className="form-control input" 
        checked={edit.reminder} 
        onChange={(e)=>setReminder(e.currentTarget.checked)}/>
      </div>
      <button type="submit" className="btn btn-block">Add Task</button>
    </form> 
    ))
  }

    </div>
  )
}

export default Edit
